mapper.select:
select s.orderCode, s.orderstat, s.totalprice, s.orderdate, s.itemName, s.shippingCode, s.receivAddr, s.receivContact, s.receiver ,s.itemNum,s.price
from ( select /*+INDEX_DESC(t_order_history PK_T_ORDER_HISTORY) */ rownum rn,h.orderCode, h.orderstat, o.totalprice, o.orderdate, i.itemName, d.shippingCode, d.receivAddr, d.receivContact, d.receiver ,o.itemNum,i.price
from t_order_history h, t_order_info o, t_item i, t_delivery d where
h.orderCode = o.orderCode and d.orderCode = h.orderCode and o.itemCode = i.itemCode and h.itemCode = i.itemCode and h.baskId = d.baskId and h.baskid = o.baskid
and h.orderStat = #{orderStat}
and #{date1} <= to_char(o.orderdate, 'yyyy-MM-dd') and to_char(o.orderdate, 'yyyy-MM-dd') <= #{date2}
and rownum <= #{pageNum} * #{amount} ) s where rn > (#{pageNum} -1)* #{amount}

mapper.update:
update t_delivery set 
shippingCode = #{shippingCode}
where orderCode = #{orderCode}

mapper.select:
select s.orderCode, s.orderstat, s.totalprice, s.orderdate, s.itemName, s.shippingCode, s.receivAddr, s.receivContact, s.receiver ,s.itemNum,s.price
from ( select /*+INDEX_DESC(t_order_history PK_T_ORDER_HISTORY) */ rownum rn,h.orderCode, h.orderstat, o.totalprice, o.orderdate, i.itemName, d.shippingCode, d.receivAddr, d.receivContact, d.receiver ,o.itemNum,i.price
from t_order_history h, t_order_info o, t_item i, t_delivery d where
h.orderCode = o.orderCode and d.orderCode = h.orderCode and o.itemCode = i.itemCode and h.itemCode = i.itemCode and h.baskId = d.baskId and h.baskid = o.baskid
and #{date1} <= to_char(o.orderdate, 'yyyy-MM-dd') and to_char(o.orderdate, 'yyyy-MM-dd') <= #{date2}
and rownum <= #{pageNum} * #{amount} ) s where rn > (#{pageNum} -1)* #{amount}

select distinct s.orderCode, s.orderstat, s.totalprice, s.orderdate, s.itemName, s.shippingCode, s.receivAddr, s.receivContact, s.receiver ,s.itemNum,s.price,s.baskId,s.prgDate,s.itemCode
from ( select /*+INDEX_DESC(t_order_history PK_T_ORDER_HISTORY) */ rownum rn,h.orderCode, h.orderstat, o.totalprice, o.orderdate, i.itemName, d.shippingCode, d.receivAddr, d.receivContact, d.receiver ,o.itemNum,i.price,h.baskId,h.prgDate,h.itemCode
from t_order_history h, t_order_info o, t_item i, t_delivery d where
h.orderCode = o.orderCode and d.orderCode = h.orderCode and o.itemCode = i.itemCode and h.itemCode = i.itemCode
and h.orderStat = #{orderStat}
and #{date1} <= to_char(o.orderdate, 'yyyy-MM-dd') and to_char(o.orderdate, 'yyyy-MM-dd') <= #{date2}
and rownum <= #{pageNum} * #{amount} ) s where rn > (#{pageNum} -1)* #{amount}
